describe("ready (event)",()=>{it("should trigger the `ready` event",e=>{const t=window.createImage();t.addEventListener("ready",t=>{expect(t.type).to.equal("ready"),e()}),new Cropper(t)}),it("should trigger the `crop` event even though default prevented",e=>{const t=window.createImage();t.addEventListener("ready",e=>{e.preventDefault()}),t.addEventListener("crop",()=>{e()}),new Cropper(t)})});