describe("zoom (event)",()=>{it("should trigger the `zoom` event",e=>{const t=window.createImage();let o;t.addEventListener("ready",()=>{o.zoom(.1)}),t.addEventListener("zoom",t=>{expect(t.type).to.equal("zoom"),e()}),o=new Cropper(t)}),it("should have correct properties in `event.detail`",e=>{const t=window.createImage();let o;t.addEventListener("ready",()=>{o.zoom(.1)}),t.addEventListener("zoom",t=>{expect(t.detail).to.be.an("object").that.has.all.keys(["ratio","oldRatio","originalEvent"]),expect(t.detail.ratio).to.be.a("number"),expect(t.detail.oldRatio).to.be.a("number"),expect(t.detail.ratio).to.be.above(0),expect(t.detail.oldRatio).to.be.above(0),expect(t.detail.ratio).to.be.above(t.detail.oldRatio),e()}),o=new Cropper(t)}),it("should not change the canvas sizes when default prevented",e=>{const t=window.createImage();let o;t.addEventListener("ready",()=>{const t=o.getCanvasData();expect(o.zoom(.1).getCanvasData()).to.deep.equal(t),e()}),t.addEventListener("zoom",e=>{e.preventDefault()}),o=new Cropper(t)})});