describe("clear (method)",()=>{it("should match the expected behaviors after cleared",e=>{const t=window.createImage(),o=new Cropper(t,{ready(){expect(o.cropped).to.be["true"],o.clear(),expect(o.cropped).to.be["false"],expect(o.getData()).to.deep.equal({x:0,y:0,width:0,height:0,rotate:0,scaleX:1,scaleY:1}),expect(o.getCropBoxData()).to.be.an("object").that.is.empty,expect(window.getComputedStyle(o.cropBox).display).to.equal("none"),e()}})}),it("should not clear when it is disabled",e=>{const t=window.createImage(),o=new Cropper(t,{ready(){o.disable().clear(),expect(o.cropped).to.be["true"],expect(o.getCropBoxData()).to.be.an("object").that.is.not.empty,expect(window.getComputedStyle(o.cropBox).display).to.not.equal("none"),e()}})})});