describe("crop (method)",()=>{it("should match the expected behaviors by default",e=>{const o=window.createImage(),t=new Cropper(o,{autoCrop:!1,ready(){expect(t.cropped).to.be["false"],expect(window.getComputedStyle(t.cropBox).display).to.equal("none"),t.crop(),expect(t.cropped).to.be["true"],expect(window.getComputedStyle(t.cropBox).display).to.not.equal("none"),e()}})}),it("should trigger the `crop` event",e=>{const o=window.createImage();o.addEventListener("crop",()=>{e()});const t=new Cropper(o,{autoCrop:!1,ready(){t.crop()}})}),it("should not crop again when it is already cropped",e=>{const o=window.createImage();let t=0;const p=new Cropper(o,{crop(){(t+=1)>1?expect.fail(1,0):(p.crop(),e())}})})});