describe("move (method)",()=>{it("should move with the given offsets",e=>{const t=window.createImage(),o=new Cropper(t,{ready(){const t=o.getCanvasData(),a=o.move(1,1).getCanvasData();expect(a.left).to.equal(t.left+1),expect(a.top).to.equal(t.top+1),e()}})}),it("should not work when it is not movable",e=>{const t=window.createImage(),o=new Cropper(t,{movable:!1,ready(){const t=o.getCanvasData(),a=o.move(1,1).getCanvasData();expect(a.left).to.equal(t.left),expect(a.top).to.equal(t.top),e()}})})});