describe("moveTo (method)",()=>{it("should move to the given coordinate",e=>{const o=window.createImage(),t=new Cropper(o,{ready(){const o=t.moveTo(100,100).getCanvasData();expect(o.left).to.equal(100),expect(o.top).to.equal(100),e()}})}),it("should not work when it is not movable",e=>{const o=window.createImage(),t=new Cropper(o,{movable:!1,ready(){const o=t.moveTo(100,100).getCanvasData();expect(o.left).to.not.equal(100),expect(o.top).to.not.equal(100),e()}})})});