describe("reset (method)",()=>{it("should reset the cropper to its initial state",e=>{const t=window.createImage(),a=new Cropper(t,{ready(){const t=a.getCanvasData(),o=a.getCropBoxData();a.setCanvasData({top:t.top+10,width:t.width-10}),expect(a.getCanvasData()).to.not.deep.equal(t),a.setCropBoxData({left:o.left+10,height:o.height-10}),expect(a.getCropBoxData()).to.not.deep.equal(o),a.reset(),expect(a.getCanvasData()).to.deep.equal(t),expect(a.getCropBoxData()).to.deep.equal(o),e()}})})});