describe("rotate (method)",()=>{it("should rotate with the given degree",e=>{const t=window.createImage(),a=new Cropper(t,{ready(){expect(a.rotate(360).getImageData().rotate).to.equal(0),expect(a.rotate(90).getImageData().rotate).to.equal(90),expect(a.rotate(-180).getImageData().rotate).to.equal(-90),e()}})}),it("should not work when it is not rotatable",e=>{const t=window.createImage(),a=new Cropper(t,{rotatable:!1,ready(){expect(a.rotate(360).getImageData().rotate).to.be.undefined,expect(a.rotate(90).getImageData().rotate).to.be.undefined,expect(a.rotate(-180).getImageData().rotate).to.be.undefined,e()}})})});