describe("scale (method)",()=>{it("should scale with the given values",e=>{const a=window.createImage(),t=new Cropper(a,{ready(){const a=t.scale(-1,-1).getImageData();expect(a.scaleX).to.equal(-1),expect(a.scaleY).to.equal(-1),e()}})}),it("should not work when it is not scalable",e=>{const a=window.createImage(),t=new Cropper(a,{scalable:!1,ready(){const a=t.scale(-1,-1).getImageData();expect(a.scaleX).to.be.undefined,expect(a.scaleY).to.be.undefined,e()}})})});