describe("scaleX (method)",()=>{it("should be scaled in the x-axis",e=>{const a=window.createImage(),t=new Cropper(a,{ready(){const a=t.scaleX(-1).getImageData();expect(a.scaleX).to.equal(-1),e()}})}),it("should not work when it is not scalable",e=>{const a=window.createImage(),t=new Cropper(a,{scalable:!1,ready(){const a=t.scaleX(-1).getImageData();expect(a.scaleX).to.be.undefined,e()}})})});