describe("setDefaults",()=>{it("should be a static method",()=>{expect(Cropper.setDefaults).to.be.a("function")}),it("should change the global default options",t=>{Cropper.setDefaults({aspectRatio:1});const e=window.createImage(),o=new Cropper(e,{aspectRatio:1,crop(){const e=o.getCropBoxData();expect(e.width).to.equal(e.height),t()}});expect(o.options.aspectRatio).to.equal(1),Cropper.setDefaults({aspectRatio:NaN})})});