describe("setDragMode (method)",()=>{it('should set the drag mode to "move"',e=>{const o=window.createImage(),t=new Cropper(o,{ready(){const{dragBox:o}=t;expect(o.dataset.cropperAction).to.equal("crop"),t.setDragMode("move"),expect(o.dataset.cropperAction).to.equal("move"),e()}})}),it('should set the drag mode to "none"',e=>{const o=window.createImage(),t=new Cropper(o,{ready(){const{dragBox:o}=t;expect(o.dataset.cropperAction).to.equal("crop"),t.setDragMode("none"),expect(o.dataset.cropperAction).to.equal("none"),e()}})})});