describe("zoomTo (method)",()=>{it("should zoom to the certain ratio",t=>{const a=window.createImage(),e=new Cropper(a,{ready(){const a=e.zoomTo(1).getImageData(),o=e.getCanvasData();expect(a.width).to.equal(a.naturalWidth),expect(o.width).to.equal(o.naturalWidth),expect(o.naturalWidth).to.equal(a.naturalWidth),t()}})}),it("should not be zoomed when it is not zoomable",t=>{const a=window.createImage(),e=new Cropper(a,{zoomable:!1,ready(){const a=e.zoomTo(1).getImageData(),o=e.getCanvasData();expect(a.width).to.not.equal(a.naturalWidth),expect(o.width).to.not.equal(o.naturalWidth),expect(o.naturalWidth).to.equal(a.naturalWidth),t()}})})});