describe("aspectRatio (option)",()=>{it("should be `NaN` by default",()=>{const t=window.createImage(),e=new Cropper(t);expect(e.options.aspectRatio).to.be.NaN}),it("should match the given aspect ratio",t=>{const e=window.createImage(),o=new Cropper(e,{aspectRatio:1,crop(){const e=o.getCropBoxData();expect(e.width).to.equal(e.height),t()}});expect(o.options.aspectRatio).to.equal(1)})});