describe("background (option)",()=>{it("should show the background by default",e=>{const o=window.createImage(),t=new Cropper(o,{ready(){expect(window.getComputedStyle(t.cropper).backgroundImage).to.not.equal("none"),e()}});expect(t.options.background).to.be["true"]}),it("should not show the background",e=>{const o=window.createImage(),t=new Cropper(o,{background:!1,ready(){expect(window.getComputedStyle(t.cropper).backgroundImage).to.equal("none"),e()}});expect(t.options.background).to.be["false"]})});