describe("center (option)",()=>{it("should show the center cross hair by default",e=>{const t=window.createImage(),o=new Cropper(t,{ready(){const t=o.cropper.querySelector(".cropper-center");expect(window.getComputedStyle(t).display).to.not.equal("none"),e()}});expect(o.options.center).to.be["true"]}),it("should hide the center cross hair",e=>{const t=window.createImage(),o=new Cropper(t,{center:!1,ready(){const t=o.cropper.querySelector(".cropper-center");expect(window.getComputedStyle(t).display).to.equal("none"),e()}});expect(o.options.center).to.be["false"]})});