describe("checkCrossOrigin (option)",()=>{const e="https://fengyuanchen.github.io/cropperjs/images/picture.jpg";it("should check cross origin by default",o=>{const i=window.createImage({src:e}),t=new Cropper(i,{ready(){expect(t.image.crossOrigin).to.equal("anonymous"),expect(t.image.src).to.include("timestamp"),o()}});expect(t.options.checkCrossOrigin).to.be["true"]}),it("should not check cross origin",o=>{const i=window.createImage({src:e}),t=new Cropper(i,{checkCrossOrigin:!1,ready(){expect(t.image.crossOrigin).to.be["null"],expect(t.image.src).to.not.include("timestamp"),o()}});expect(t.options.checkCrossOrigin).to.be["false"]}),it("should not add timestamp when the image has `crossOrigin` attribute",o=>{const i=window.createImage({src:e,crossOrigin:"anonymous"}),t=new Cropper(i,{ready(){expect(t.image.crossOrigin).to.equal("anonymous"),expect(t.image.src).to.not.include("timestamp"),o()}})})});