describe("checkOrientation (option)",()=>{const e="/base/docs/images/picture-3.jpg";it("should check orientation be default",t=>{const o=window.createImage({src:e}),a=new Cropper(o,{ready(){expect(a.getData().rotate).to.not.equal(0),t()}});expect(a.options.checkOrientation).to.be["true"]}),it("should not check orientation",t=>{const o=window.createImage({src:e}),a=new Cropper(o,{checkOrientation:!1,ready(){expect(a.getData().rotate).to.equal(0),t()}});expect(a.options.checkOrientation).to.be["false"]}),it("should not check orientation when it is not rotatable and not scalable",()=>{const t=window.createImage({src:e}),o=new Cropper(t,{rotatable:!1,scalable:!1});expect(o.options.checkOrientation).to.be["false"]})});