describe("crop (option)",()=>{it("should be `null` be default",()=>{const e=window.createImage(),t=new Cropper(e);expect(t.options.crop).to.be["null"]}),it("should execute the `crop` hook function",e=>{const t=window.createImage(),o=new Cropper(t,{crop(t){expect(t.type).to.equal("crop"),expect(t.detail).to.be.an("object").that.has.all.keys(["x","y","width","height","rotate","scaleX","scaleY"]),expect(t.detail.x).to.be.a("number"),expect(t.detail.y).to.be.a("number"),expect(t.detail.width).to.be.a("number"),expect(t.detail.height).to.be.a("number"),expect(t.detail.rotate).to.be.a("number"),expect(t.detail.scaleX).to.be.a("number"),expect(t.detail.scaleY).to.be.a("number"),e()}});expect(o.options.crop).to.be.a("function")})});