describe("cropBoxResizable (option)",()=>{it("should be resizable by default",e=>{const o=window.createImage(),r=new Cropper(o,{ready(){Array.from(r.cropper.querySelectorAll(".cropper-line, .cropper-point")).forEach(e=>{expect(window.getComputedStyle(e).display).to.not.equal("none")}),e()}});expect(r.options.cropBoxResizable).to.be["true"]}),it("should not be resizable",e=>{const o=window.createImage(),r=new Cropper(o,{cropBoxResizable:!1,ready(){Array.from(r.cropper.querySelectorAll(".cropper-line, .cropper-point")).forEach(e=>{expect(window.getComputedStyle(e).display).to.equal("none")}),e()}});expect(r.options.cropBoxResizable).to.be["false"]})});