describe("cropend (option)",()=>{const e=window.PointerEvent?"pointerdown":"mousedown",t=window.PointerEvent?"pointermove":"mousemove",o=window.PointerEvent?"pointerup":"mouseup";it("should be `null` be default",()=>{const e=window.createImage(),t=new Cropper(e);expect(t.options.cropend).to.be["null"]}),it("should execute the `cropend` hook function",n=>{const i=window.createImage(),c=new Cropper(i,{ready(){const{dragBox:n}=c;n.dispatchEvent(window.createEvent(e)),n.dispatchEvent(window.createEvent(t)),n.dispatchEvent(window.createEvent(o))},cropend(e){expect(e.type).to.equal("cropend"),expect(e.detail).to.be.an("object").that.has.all.keys(["action","originalEvent"]),expect(e.detail.action).to.equal("crop"),expect(e.detail.originalEvent.type).to.equal(o),n()}});expect(c.options.cropend).to.be.a("function")})});