describe("data (option)",()=>{it("should be null by default",()=>{const e=window.createImage(),t=new Cropper(e);expect(t.options.data).to.be["null"]}),it("should match the given data",e=>{const t=window.createImage(),a={x:16,y:9,width:160,height:90},o=new Cropper(t,{data:a,ready(){const t=o.getData(!0);expect(t.x).to.equal(a.x),expect(t.y).to.equal(a.y),expect(t.width).to.equal(a.width),expect(t.height).to.equal(a.height),e()}});expect(o.options.data).to.deep.equal(a)})});