describe("dragMode (option)",()=>{it('should be "crop" by default',e=>{const o=window.createImage(),t=new Cropper(o,{ready(){expect(t.dragBox.dataset.cropperAction).to.equal("crop"),e()}});expect(t.options.dragMode).to.equal("crop")}),it('should be "move"',e=>{const o=window.createImage(),t=new Cropper(o,{dragMode:"move",ready(){expect(t.dragBox.dataset.cropperAction).to.equal("move"),e()}});expect(t.options.dragMode).to.equal("move")}),it('should be "none"',e=>{const o=window.createImage(),t=new Cropper(o,{dragMode:"none",ready(){expect(t.dragBox.dataset.cropperAction).to.equal("none"),e()}});expect(t.options.dragMode).to.equal("none")})});