describe("guides (option)",()=>{it("should show the guides by default",e=>{const o=window.createImage(),r=new Cropper(o,{ready(){Array.from(r.cropper.querySelectorAll(".cropper-dashed")).forEach(e=>{expect(window.getComputedStyle(e).display).to.not.equal("none")}),e()}});expect(r.options.guides).to.be["true"]}),it("should hide the guides",e=>{const o=window.createImage(),r=new Cropper(o,{guides:!1,ready(){Array.from(r.cropper.querySelectorAll(".cropper-dashed")).forEach(e=>{expect(window.getComputedStyle(e).display).to.equal("none")}),e()}});expect(r.options.guides).to.be["false"]})});