describe("initialAspectRatio (option)",()=>{it("should be `NaN` by default",()=>{const t=window.createImage(),e=new Cropper(t);expect(e.options.initialAspectRatio).to.be.NaN}),it("should match the given aspect ratio",t=>{const e=window.createImage(),o=new Cropper(e,{initialAspectRatio:1,ready(){const e=o.getCropBoxData();expect(e.width).to.equal(e.height),t()}});expect(o.options.initialAspectRatio).to.equal(1)}),it("should allow to change the aspect ratio",t=>{const e=window.createImage(),o=new Cropper(e,{initialAspectRatio:2,ready(){let e=o.getCropBoxData();e.width=e.height,e=o.setCropBoxData(e).getCropBoxData(),expect(e.width).to.equal(e.height),t()}})})});