describe("minCanvasHeight (option)",()=>{it("should be `0` by default",()=>{const e=window.createImage(),t=new Cropper(e);expect(t.options.minCanvasHeight).to.equal(0)}),it("should match the given minimum size",e=>{const t=window.createImage(),a=270,i=new Cropper(t,{minCanvasHeight:a,ready(){const t=i.setCanvasData({height:180}).getCanvasData();expect(t.height).to.equal(a),e()}});expect(i.options.minCanvasHeight).to.equal(a)})});