describe("minCanvasWidth (option)",()=>{it("should be `0` by default",()=>{const e=window.createImage(),t=new Cropper(e);expect(t.options.minCanvasWidth).to.equal(0)}),it("should match the given minimum size",e=>{const t=window.createImage(),a=480,i=new Cropper(t,{minCanvasWidth:a,ready(){const t=i.setCanvasData({width:320}).getCanvasData();expect(t.width).to.equal(a),e()}});expect(i.options.minCanvasWidth).to.equal(a)})});