describe("minContainerWidth (option)",()=>{it("should be `200` by default",()=>{const e=window.createImage(),t=new Cropper(e);expect(t.options.minContainerWidth).to.equal(200)}),it("should match the given minimum size",e=>{const t=window.createImage();t.parentElement.style.width="160px";const n=320,i=new Cropper(t,{minContainerWidth:n,ready(){expect(i.getContainerData().width).to.equal(n),e()}});expect(i.options.minContainerWidth).to.equal(n)})});