describe("minCropBoxWidth (option)",()=>{it("should be `0` by default",()=>{const o=window.createImage(),e=new Cropper(o);expect(e.options.minCropBoxWidth).to.equal(0)}),it("should match the given minimum size",o=>{const e=window.createImage(),t=300,i=new Cropper(e,{minCropBoxWidth:t,ready(){const e=i.setCropBoxData({width:200}).getCropBoxData();expect(e.width).to.equal(t),o()}});expect(i.options.minCropBoxWidth).to.equal(t)})});