describe("modal (option)",()=>{it("should show the modal by default",e=>{const o=window.createImage(),t=new Cropper(o,{ready(){expect(t.dragBox.className).to.include("cropper-modal"),e()}});expect(t.options.modal).to.be["true"]}),it("should not show the modal",e=>{const o=window.createImage(),t=new Cropper(o,{modal:!1,ready(){expect(t.dragBox.className).to.not.include("cropper-modal"),e()}});expect(t.options.modal).to.be["false"]})});