describe("movable (option)",()=>{it("should be movable by default",e=>{const t=window.createImage(),o=new Cropper(t,{ready(){const t=o.getCanvasData(),a=o.move(10,10).getCanvasData();expect(a.left).to.equal(t.left+10),expect(a.top).to.equal(t.top+10),e()}});expect(o.options.movable).to.be["true"]}),it("should not be movable",e=>{const t=window.createImage(),o=new Cropper(t,{movable:!1,ready(){const t=o.getCanvasData(),a=o.move(10,10).getCanvasData();expect(a.left).to.equal(t.left),expect(a.top).to.equal(t.top),e()}});expect(o.options.movable).to.be["false"]})});