describe("preview (option)",()=>{it("should be an empty string be default",()=>{const e=window.createImage(),t=new Cropper(e);expect(t.options.preview).to.be.a("string").that.is.empty}),it("should support element",e=>{const t=window.createImage(),n=document.createElement("div");t.ownerDocument.body.appendChild(n);const l=new Cropper(t,{preview:n,ready(){expect(n.firstElementChild).to.not.be["null"],expect(n.firstElementChild.src).to.equal(t.src),e()}});expect(l.options.preview).to.equal(n)}),it("should support array of elements",e=>{const t=window.createImage(),n=document.createElement("ul"),l=document.createElement("li"),o=document.createElement("li"),r=[l,o];n.appendChild(l),n.appendChild(o),t.ownerDocument.body.appendChild(n);const i=new Cropper(t,{preview:r,ready(){expect(l.firstElementChild).to.not.be["null"],expect(o.firstElementChild).to.not.be["null"],expect(l.firstElementChild.src).to.equal(t.src),expect(o.firstElementChild.src).to.equal(t.src),e()}});expect(i.options.preview).to.equal(r)}),it("should support `NodeList` object",e=>{const t=window.createImage(),n=document.createElement("ul"),l=document.createElement("li"),o=document.createElement("li");n.appendChild(l),n.appendChild(o),t.ownerDocument.body.appendChild(n);const r=new Cropper(t,{preview:n.children,ready(){expect(l.firstElementChild).to.not.be["null"],expect(o.firstElementChild).to.not.be["null"],expect(l.firstElementChild.src).to.equal(t.src),expect(o.firstElementChild.src).to.equal(t.src),e()}});expect(r.options.preview).to.equal(n.children)}),it("should support selector",e=>{const t=window.createImage(),n=document.createElement("ul"),l=document.createElement("li"),o=document.createElement("li");l.className="preview",o.className="preview",n.appendChild(l),n.appendChild(o),t.ownerDocument.body.appendChild(n);const r=new Cropper(t,{preview:".preview",ready(){expect(l.firstElementChild).to.not.be["null"],expect(o.firstElementChild).to.not.be["null"],expect(l.firstElementChild.src).to.equal(t.src),expect(o.firstElementChild.src).to.equal(t.src),e()}});expect(r.options.preview).to.equal(".preview")})});