describe("restore (option)",()=>{it("should restore the cropped area after resize the window be default",e=>{const t=window.createImage(),o=new Cropper(t,{ready(){const r=o.zoomTo(1).getData(!0),a=o.getContainerData();t.parentElement.style.width=`${a.width-10}px`,t.parentElement.style.height=`${a.height-10}px`,window.dispatchEvent(window.createEvent("resize")),expect(o.getData(!0)).to.deep.equal(r),e()}});expect(o.options.restore).to.be["true"]}),it("should not be restore",e=>{const t=window.createImage(),o=new Cropper(t,{restore:!1,ready(){const r=o.zoomTo(1).getData(!0),a=o.getContainerData();t.parentElement.style.width=`${a.width/2}px`,window.dispatchEvent(window.createEvent("resize")),expect(o.getData(!0)).to.not.deep.equal(r),e()}});expect(o.options.restore).to.be["false"]})});