describe("rotatable (option)",()=>{it("should be rotatable by default",e=>{const t=window.createImage(),a=new Cropper(t,{ready(){expect(a.rotate(90).getImageData().rotate).to.equal(90),e()}});expect(a.options.rotatable).to.be["true"]}),it("should not be rotatable",e=>{const t=window.createImage(),a=new Cropper(t,{rotatable:!1,ready(){expect(a.rotate(90).getImageData().rotate).to.be.undefined,e()}});expect(a.options.rotatable).to.be["false"]})});