describe("scalable (option)",()=>{it("should be scalable by default",e=>{const a=window.createImage(),t=new Cropper(a,{ready(){const a=t.scale(-1,-1).getImageData();expect(a.scaleX).to.equal(-1),expect(a.scaleY).to.equal(-1),e()}});expect(t.options.scalable).to.be["true"]}),it("should not be scalable",e=>{const a=window.createImage(),t=new Cropper(a,{scalable:!1,ready(){const a=t.scale(-1,-1).getImageData();expect(a.scaleX).to.be.undefined,expect(a.scaleY).to.be.undefined,e()}});expect(t.options.scalable).to.be["false"]})});