describe("toggleDragModeOnDblclick (option)",()=>{it("should toggle drag mode on double click",e=>{const c=window.createImage(),o=new Cropper(c,{ready(){const{dragBox:c}=o;expect(c.className).include("cropper-crop"),expect(c.dataset.cropperAction).to.equal("crop"),c.dispatchEvent(window.createEvent("dblclick")),expect(c.className).include("cropper-move"),expect(c.dataset.cropperAction).to.equal("move"),e()}});expect(o.options.toggleDragModeOnDblclick).to.be["true"]}),it("should not toggle drag mode on double click",e=>{const c=window.createImage(),o=new Cropper(c,{toggleDragModeOnDblclick:!1,ready(){const{dragBox:c}=o;expect(c.className).include("cropper-crop"),expect(c.dataset.cropperAction).to.equal("crop"),c.dispatchEvent(window.createEvent("dblclick")),expect(c.className).include("cropper-crop"),expect(c.dataset.cropperAction).to.equal("crop"),e()}});expect(o.options.toggleDragModeOnDblclick).to.be["false"]})});