describe("zoom (option)",()=>{it("should be `null` be default",()=>{const e=window.createImage(),o=new Cropper(e);expect(o.options.zoom).to.be["null"]}),it("should execute the `zoom` hook function",e=>{const o=window.createImage(),t=new Cropper(o,{ready(){t.zoom(.1)},zoom(o){expect(o.type).to.equal("zoom"),expect(o.detail).to.be.an("object").that.has.all.keys(["ratio","oldRatio","originalEvent"]),expect(o.detail.ratio).to.be.a("number"),expect(o.detail.oldRatio).to.be.a("number"),expect(o.detail.ratio).to.be.above(0),expect(o.detail.oldRatio).to.be.above(0),expect(o.detail.ratio).to.be.above(o.detail.oldRatio),e()}});expect(t.options.zoom).to.be.a("function")}),it("should not change the canvas sizes when default prevented",e=>{const o=window.createImage(),t=new Cropper(o,{ready(){const o=t.getCanvasData();expect(t.zoom(.1).getCanvasData()).to.deep.equal(o),e()},zoom(e){e.preventDefault()}})})});