describe("zoomOnWheel (option)",()=>{it("should zoom on wheel by default",e=>{const o=window.createImage(),t=new Cropper(o,{ready(){t.cropper.dispatchEvent(window.createEvent("wheel"))},zoom(){e()}});expect(t.options.zoomOnWheel).to.be["true"]}),it("should not zoom on wheel",e=>{const o=window.createImage(),t=new Cropper(o,{zoomOnWheel:!1,ready(){t.cropper.dispatchEvent(window.createEvent("wheel")),e()},zoom(){expect.fail(1,0)}});expect(t.options.zoomOnWheel).to.be["false"]})});