describe("zoomable (option)",()=>{it("should be zoomable by default",e=>{const t=window.createImage(),o=new Cropper(t,{ready(){const t=o.getCanvasData(),a=o.zoom(.1).getCanvasData();expect(a.width).to.be.above(t.width),expect(a.height).to.be.above(t.height),e()}});expect(o.options.zoomable).to.be["true"]}),it("should not be zoomable",e=>{const t=window.createImage(),o=new Cropper(t,{zoomable:!1,ready(){const t=o.getCanvasData(),a=o.zoom(.1).getCanvasData();expect(a.width).to.equal(t.width),expect(a.height).to.equal(t.height),e()}});expect(o.options.zoomable).to.be["false"]})});