function valueToPoint(t,e,a){var l=0,s=.8*-t,n=2*Math.PI/a*e,o=Math.cos(n),i=Math.sin(n),u,p;return{x:l*o-s*i+100,y:l*i+s*o+100}}var stats=[{label:"A",value:100},{label:"B",value:100},{label:"C",value:100},{label:"D",value:100},{label:"E",value:100},{label:"F",value:100}];Vue.component("polygraph",{props:["stats"],template:"#polygraph-template",computed:{points:function(){var t=this.stats.length;return this.stats.map(function(e,a){var l=valueToPoint(e.value,a,t);return l.x+","+l.y}).join(" ")}},components:{"axis-label":{props:{stat:Object,index:Number,total:Number},template:"#axis-label-template",computed:{point:function(){return valueToPoint(+this.stat.value+10,this.index,this.total)}}}}}),new Vue({el:"#demo",data:{newLabel:"",stats:stats},methods:{add:function(t){t.preventDefault(),this.newLabel&&(this.stats.push({label:this.newLabel,value:100}),this.newLabel="")},remove:function(t){this.stats.length>3?this.stats.splice(this.stats.indexOf(t),1):alert("Can't delete more!")}}});